import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Main from './Components/Main';
import './Components/style.css';
import LoginPage from './Components/LoginPage';

function PrivateRoute({ element, isAuthenticated }) {
  return isAuthenticated ? element : <Navigate to="/login" />;
}


function App() {
  // Define the checkAuthenticated function
function checkAuthenticated() {
  // Implement your authentication logic here
  // Return true if the user is authenticated, false otherwise
  // For example, you might check for a token in localStorage or a session cookie
  const token = localStorage.getItem('token');
  return !!token;
}

const isAuthenticated = checkAuthenticated(); // Check if the user is authenticated

if (isAuthenticated) {
  // User is authenticated, perform actions accordingly
  return isAuthenticated ? element : <Navigate to="/Main"/>
} else {
  // User is not authenticated, handle unauthorized access
  return isAuthenticated ? element : <Navigate to="/"/> 
}

  return (
    <>
    {/* <Router>
    <Routes>

        <Route path="/" element={<LoginPage/>} />
        <Route path="/Main" element={<Main/>} />
      </Routes>
    </Router> */}
    <Routes>
      <Route path="/" element={<LoginPage />} />
      <PrivateRoute path="/Main" element={<Main />} isAuthenticated={isAuthenticated} />
    </Routes>

    </>
  );
}

export default App;
